import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import fetch from 'isomorphic-fetch';

class Certificate extends React.PureComponent {
  tryingThis = () => {
    console.log('heyyy');
    event.preventDefault();
    fetch('/api/thoughts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        message
      })
    })
      .then(result => console.log)
      .catch(error => console.error);
  };

  render() {
    const { error, certificate } = this.props;

    return (
      <Container>
        <Row className="justify-content-center">
          <Col sm={6} xs={12}>
            <Card border="info">
              <Card.Header>
                <FontAwesomeIcon icon={['fas', 'address-card']} />
                <span> Your certificate</span>
              </Card.Header>
              <Card.Body>
                <Card.Text>{certificate || 'No certificate found'}</Card.Text>
              </Card.Body>
              <Card.Footer>
                <Button variant="primary" onClick={this.tryingThis}>
                  Check
                </Button>
              </Card.Footer>
            </Card>
          </Col>

          <Col>
            <Card border="primary">
              <Card.Header>Actions</Card.Header>
              <Card.Body>
                <Card.Text>
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    );
  }
}

Certificate.getInitialProps = async ({ req }) => {
  const baseURL = req ? `${req.protocol}://${req.get('Host')}` : '';
  const res = await fetch(`${baseURL}/api/register`);

  return {
    certificate: res.status === 200 && (await res.json()),
    error: res.status != 200
  };
};

export default Certificate;
